def preparing(bairro, quartos, area):
    bairro_prep = bairro_to_onehot[bairro.lower()]   
    quartos_prep = int(quartos)                
    area_prep = int(area)
    features = [bairro_prep, quartos_prep, area_prep].reshape(1, -1)
    return features
    
